// src/app/services/filmes.service.ts
import { Injectable } from '@angular/core';
import { Observable, of } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class FilmesService {
  private categorias = ['Ação', 'Comédia', 'Drama', 'Ficção Científica'];

  private filmesPorCategoria: { [key: string]: any[] } = {
    'Ação': [
      { id: 1, nome: 'Guerra Civil', capa: 'data:image/jpeg;base64,' },
      { id: 2, nome: 'Eu sou a Lenda', capa: 'data:image/jpeg;base64,' }
    ],
    'Comédia': [
      { id: 3, nome: 'Os Farofeiros', capa: 'data:image/jpeg;base64,' },
      { id: 4, nome: 'Férias Trocadas', capa: 'data:image/jpeg;base64,' }
    ],
    'Drama': [
      { id: 5, nome: 'Nosso Sonho', capa: 'data:image/jpeg;base64,' },
      { id: 6, nome: 'Minha Culpa', capa: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTkGTnSjfb2DwBS9srZJxUfb-N_RQEIS3JuMg&s' }
    ],
    'Ficção Científica': [
      { id: 7, nome: 'A Estrada', capa: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSWJluxOw9D4DMrokfiUAzhpSrfvGu-EmnS1w&s' },
      { id: 8, nome: 'Reset', capa: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSrQXOw0UDF-jYqcL6S6FWhd0GzEScblfz97A&s' }
    ]
  };

  constructor() { }

  getCategorias(): Observable<string[]> {
    return of(this.categorias);
  }

  getFilmesPorCategoria(categoria: string): Observable<any[]> {
    return of(this.filmesPorCategoria[categoria] || []);
  }
}
